mapper.select:
SELECT COUNT(*) AS status_count FROM ( -- 친구인지 여부를 확인 SELECT 1 AS status FROM FRIENDSHIPS WHERE user_id = #{loginUserId} AND friend_id = #{friendId}
UNION ALL
-- 친구 요청 상태를 확인 SELECT 2 AS status FROM REQUESTFRIEND WHERE (USER_ID = #{loginUserId} AND FRIEND_ID = #{friendId}) OR (USER_ID = #{friendId} AND FRIEND_ID = #{loginUserId})
) combined

SELECT friend_id
FROM friendships
WHERE user_id = #{loginUserId}
